// Animation component
const AnimatedCard: React.FC<{ children: React.ReactNode; delay?: number }> = ({ children, delay = 0 }) => {
  const [isVisible, setIsVisible] = useState(false);
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setTimeout(() => setIsVisible(true), delay);
          observer.disconnect();
        }
      },
      { threshold: 0.1 }
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => observer.disconnect();
  }, [delay]);

  return (
    <div
      ref={ref}
      className={`transition-all duration-700 transform ${
        isVisible
          ? 'opacity-100 translate-y-0'
          : 'opacity-0 translate-y-10'
      }`}
    >
      {children}
    </div>
  );
};

// Animated feature icon
const AnimatedFeature: React.FC<{ icon: string; text: string; color: string }> = ({ icon, text, color }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className="relative group"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className={`relative z-10 p-4 rounded-2xl bg-white shadow-lg border border-gray-100 transition-all duration-300 transform ${isHovered ? '-translate-y-2 shadow-xl' : ''}`}>
        <div className={`text-4xl mb-3 transition-transform duration-300 ${isHovered ? 'scale-110' : ''}`}>
          {icon}
        </div>
        <div className={`font-semibold text-gray-800 transition-colors duration-300 ${isHovered ? 'text-blue-600' : ''}`}>
          {text}
        </div>
      </div>
      <div
        className={`absolute inset-0 rounded-2xl blur transition-all duration-500 opacity-0 group-hover:opacity-100 ${color}`}
        style={{
          transform: isHovered ? 'scale(1.05)' : 'scale(1)',
        }}
      />
    </div>
  );
};

// Floating animation for security icons
const FloatingIcon: React.FC<{ icon: string; delay: number }> = ({ icon, delay }) => {
  return (
    <div
      className="absolute text-2xl opacity-20 animate-float"
      style={{
        animationDelay: `${delay}s`,
        animationDuration: '3s',
      }}
    >
      {icon}
    </div>
  );
};

